INSERT INTO `highlight` (`hi_id`, `hi_title`, `hi_content`, `hi_link`, `hi_modified`) VALUES
(1, 'Produk &amp; Jasa UUS', '&lt;h2&gt;&lt;strong&gt;Produk dan Jasa UUS&lt;/strong&gt;&lt;/h2&gt;&lt;address&gt;&lt;p&gt;&lt;strong&gt;Produk Funding:&lt;/strong&gt;&lt;/p&gt;&lt;ol&gt;&lt;li&gt;Giro Wadiah&lt;/li&gt;&lt;li&gt;Giro Mudharabah&lt;/li&gt;&lt;li&gt;Tabungan Syariah&lt;/li&gt;&lt;li&gt;Tabungan Kemitraan&lt;/li&gt;&lt;li&gt;Tabungan Hatam&lt;/li&gt;&lt;li&gt;Deposito Mudharabah&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;strong&gt;Produk Financing:&lt;/strong&gt;&lt;/p&gt;&lt;ol&gt;&lt;li&gt;Graha Berkah iB&lt;/li&gt;&lt;li&gt;Oto Berkah iB&lt;/li&gt;&lt;li&gt;Modal Kerja Berkah iB&lt;/li&gt;&lt;li&gt;Gadai Emas Berkah iB&lt;/li&gt;&lt;/ol&gt;&lt;p&gt;&lt;strong&gt;Produk Fee Base Income:&lt;/strong&gt;&lt;/p&gt;&lt;ol&gt;&lt;li&gt;SKN-BI&lt;/li&gt;&lt;li&gt;BI-RTGS&lt;/li&gt;&lt;li&gt;Pembayaran Listrik&lt;/li&gt;&lt;li&gt;Surat Keterangan Bank&lt;/li&gt;&lt;/ol&gt;&lt;/address&gt;', 'http://banksulselbar.co.id/beta/page/produk-dan-jasa-uus', '2016-09-05 13:57:48'),
(2, 'Garansi Bank', '&lt;h1&gt;Garansi Bank&lt;/h1&gt;&lt;p&gt;&lt;strong&gt;Tujuan Garansi Bank&lt;/strong&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Untuk kepentingan kontraktor dalam rangka proyek di dalam negeri&lt;/li&gt;&lt;li&gt;Untuk kepentingan kontraktor dalam rangka proyek di luar negeri yang diselenggarakan oleh Pemerintah Indonesia.&lt;/li&gt;&lt;li&gt;Untuk kepentingan pelaku transaksi perdagangan umum.&lt;/li&gt;&lt;li&gt;Eksportir dan Importir dalam rangka menunjang kelancaran kegiatan ekspor dan impor.&lt;/li&gt;&lt;/ul&gt;&lt;p&gt;&amp;nbsp;&lt;br /&gt;&lt;strong&gt;Jenis Garansi Bank&lt;/strong&gt;&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Garansi Bank untuk pelelangan (Bid Bond)&lt;/li&gt;&lt;li&gt;Garansi Bank untuk Pelaksanaan (Performance Bond)&lt;/li&gt;&lt;li&gt;Garansi Bank untuk Uang Muka (Advanced Payment Bond)&lt;/li&gt;&lt;li&gt;Garansi Bank untuk Pemeliharaan (Maintenance Bond)&lt;/li&gt;&lt;li&gt;Garansi Bank untuk Transaksi Perdagangan Umum&lt;/li&gt;&lt;li&gt;Garansi bank untuk Penangguhan Bea Masuk (Customs Guarantee)&lt;/li&gt;&lt;li&gt;Garansi Bank untuk Pengapalan (Shipping Guarantee)&lt;/li&gt;&lt;/ul&gt;', 'http://banksulselbar.co.id/beta/page/garansi-bank', '2016-08-24 17:10:59'),
(3, 'ATM', '&lt;h1&gt;ATM&lt;/h1&gt;&lt;p&gt;Kami menawarkan Kartu ATM Bank Sulselbar sebagai bentuk kenyamanan dan kemudahan yang dapat dinikmati oleh nasabah kami sebagai pemilik Tabungan Bank Sulselbar.Kartu ATM Bank Sulselbar kini telah tergabung dalam jaringan ATM Bersama dan jaringan ATM Prima yang dapat diakses pada lebih dari 49.000 jaringan ATM Bersama dan 73.000 jarin-gan ATM Prima untuk transaksi informasi saldo, tarik tunai dan transfer.&lt;/p&gt;&lt;p&gt;&amp;ldquo;Kartu ATM Bank Sulselbar membuat segalanya menjadi mudah&amp;rdquo;.&lt;/p&gt;&lt;p&gt;FITUR LAYANAN KARTU ATM Bank Sulselbar&lt;/p&gt;&lt;p&gt;Kartu ATM Bank Sulselbar memiliki berbagai manfaat dan kemudahan akses transaksi perbankan antara lain :&lt;/p&gt;&lt;ol&gt;&lt;li&gt;Informasi Saldo&lt;/li&gt;&lt;li&gt;Pergantian PIN&lt;/li&gt;&lt;li&gt;Penarikan&amp;nbsp; Uang Tunai&lt;/li&gt;&lt;li&gt;Transfer Uang antar Bank peserta ATM BERSAMA dan ATM PRIMA&lt;/li&gt;&lt;li&gt;Transfer antar rekening Bank Sulselbar.&lt;/li&gt;&lt;/ol&gt;', 'http://banksulselbar.co.id/beta/page/atm', '2016-08-24 17:10:41'),
(4, 'Syariah', '&lt;h1&gt;Simpanan Investasi Nasabah&lt;/h1&gt;&lt;p&gt;&lt;strong&gt;Giro Syariah (Prinsip Wadiah)&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;Adalah sarana penyimpanan dana dalam bentuk mata uang rupiah dengan pengelolaan berdasarkan prinsip titipan (Wadiah yad Dhamanah) dimana dana Nasabah akan dikelola secara amanah dan dijamin penarikannya. Giro Syariah dirancang khusus untuk memberikan jaminan keamanan dan kemudahan serta ketersediaan dana setiap saat guna membantu kelancaran transaksi usaha Nasabah, dengan penarikan melalui Cek/Bilyet Giro.&lt;/p&gt;&lt;p&gt;&lt;strong&gt;Tabungan Tandamata Syariah (Prinsip Wadiah)&lt;/strong&gt;&lt;/p&gt;&lt;p&gt;Yaitu Simpanan Nasabah (perorangan) di Bank Sulsel Syariah dalam bentuk Tabungan dalam bentuk mata uang rupiah dengan pengelolaan berdasarkan prinsip Titipan (Wadiah Yad Dhamanah) dimana dana Nasabah akan dikelola secara amanah dan dijamin penarikannya. Penyetoran dan penarikan dapat diambil setiap saat dengan fasilitas ATM Bersama dan On Line di seluruh Kantor Bank Sulsel.&lt;/p&gt;', 'http://banksulselbar.co.id/beta/page/syariah', '2016-08-24 17:10:29'),
(5, 'Jasa Bank', '&lt;div&gt;&lt;div&gt;&lt;div&gt;&lt;div&gt;&lt;h2&gt;&lt;strong&gt;Jasa Bank&lt;/strong&gt;&lt;/h2&gt;&lt;p&gt;Untuk memberikan jasa layanan yang optimal sesuai dengan kebutuhan masyarakat dan nasabah melalui upaya peningkatan teknologi, perluasan jaringan kantor, dan kemitraan dengan lembaga/badan usaha/instansi lainnya sehingga diharapkan akan memberikan kontribusi yang semakin meningkat terhadap kinerja Bank Sulselbar yang berkaitan dengan fee based income, Bank Sulselbar memiliki jasa-jasa perbankan lainnya sebagai berikut:&lt;/p&gt;&lt;ul&gt;&lt;li&gt;Kiriman Uang&lt;/li&gt;&lt;li&gt;Inkasso&lt;/li&gt;&lt;li&gt;Transfer BI-RTGS&lt;/li&gt;&lt;li&gt;Bank Garansi (Jaminan Bank)&lt;/li&gt;&lt;li&gt;Surat Keterangan Bank (Referensi Bank)&lt;/li&gt;&lt;li&gt;Penerimaan Pembayaran telepon (Telkom), Listrik, air minum (PAM),&lt;/li&gt;&lt;li&gt;Telepon seluler (Telkomsel dan Indosat), pajak;&lt;/li&gt;&lt;li&gt;Pembayaran gaji/pensiunanBank Penerima setoran biaya penyelengaraan ibadah haji (BPIH)&lt;/li&gt;&lt;li&gt;Electronic Banking : &amp;raquo;&amp;raquo; ATM &amp;amp; &amp;raquo;&amp;raquo; SMS Banking&lt;/li&gt;&lt;/ul&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;&lt;/div&gt;', 'http://banksulselbar.co.id/beta/page/jasa-bank', '2016-08-24 17:10:14'),
(6, 'Pinjaman', '&lt;h1&gt;Pinjaman&lt;/h1&gt;&lt;p&gt;Kredit Umum Lainnya (KUL) adalah kredit yang diberikan kepada Pegawai Negeri Sipil (PNS) dan Calon Pegawai &amp;nbsp;Negeri &amp;nbsp;Sipil &amp;nbsp;(CPNS) yang gajinya ditatausahakan &amp;nbsp;atau dibayarkan melalui PT. Bank Sulselbar&lt;/p&gt;', 'http://banksulselbar.co.id/beta/page/kredit-umum-lainnya-kul', '2016-08-24 17:10:03'),
(7, 'Simpanan', '&lt;h1&gt;Simpanan SIMPEDA&lt;/h1&gt;&lt;ul&gt;&lt;li&gt;Merupakan tabungan yang memberikan suku bunga kompetitif&lt;/li&gt;&lt;li&gt;Rekening dapat diakses melalui seluruh Kantor Cabang&lt;/li&gt;&lt;li&gt;Dilengkapi dengan kartu ATM/Debit /KPE bagi PNS&lt;/li&gt;&lt;li&gt;Atas bunga yang didapatkan, dikenakan pajak sesuai dengan ketentuan Pemerintah&lt;/li&gt;&lt;li&gt;Setoran awal minimum Rp. 50.000,-&lt;/li&gt;&lt;li&gt;Saldo minimum Rp. 50.000,-&lt;/li&gt;&lt;li&gt;Tersedia layanan informasi transaksi melalui SMS Banking terkait dengan transaksi &amp;nbsp;rekening&lt;/li&gt;&lt;li&gt;Media pelaporan transaksi menggunakan buku tabungan&lt;/li&gt;&lt;li&gt;Nasabah SIMPEDA Bank Sulselbar diikutsertakan dalam undian SIMPEDA nasional yang &amp;nbsp;diundi 2 (dua ) kali setahun&lt;/li&gt;&lt;/ul&gt;', 'http://banksulselbar.co.id/beta/page/simpeda', '2016-08-24 17:09:37');
